insert into tipoeventos(nombre,descripcion) values('Accidente','	Accidentes de transporte vehicular, férreo, aéreo o naval, etc.');
insert into tipoeventos(nombre,descripcion) values('Avalancha','	Desprendimiento de masas de hielo y/o nieve. ');
insert into tipoeventos(nombre,descripcion) values('Avenida torrencial','	Flujo violento de agua en una cuenca, a veces reportado como creciente (súbita, rápida), o como torrente. Se aplica cuando en los reportes aparece como -avalancha-, cuando la avenida transporta troncos de árboles y/o abundantes sedimentos desde finos hasta bloques de roca. Pueden ser generados por lluvias, por ruptura de represamientos o por abundantes deslizamientos sobre una cuenca. Excluye los aludes, porque éstos implican desprendimiento de hielo o nieve. ');
insert into tipoeventos(nombre,descripcion) values('Biológico','	Mortandad o migración de especies biológicas. Pueden, en última instancia, estar asociados a contaminación o a cambios drásticos de parámetros ambientales. Un ejemplo es la -marea roja-, el calentamiento de las aguas por el fenómeno de El Niño o disminución de las cantidades óptimas de oxígeno por obstáculos que el ser humano impone a ciertas especies, como los diques o presas.');
insert into tipoeventos(nombre,descripcion) values('Cambio línea de costa','	Variación de la línea costera y/o de las zonas marítimas próximas a la costa. Incluye formación y destrucción de islas, playas y barras de arena, erosión de acantilados, con efectos sobre poblaciones, sobre la navegación, etc.');
insert into tipoeventos(nombre,descripcion) values('Colapso estructural	','Daño o colapso de cualquier tipo de estructuras (incluidas aquellas relacionadas con las redes eléctricas, acueducto o alcantarillado), debido a fenómenos como sobrecargas en escenarios públicos, puentes, falta de mantenimiento, fatiga del material, diseños inadecuados. Incluye daños en estructuras, que sin llevarlas al colapso, las inhabilitan y que suelen ser reportadas como -fallas- (en el sentido de falla estructural). Los daños en estructuras inducidos por fenómenos naturales (por ejemplo: deslizamientos, inundaciones, etc.) se reportan como efectos de ellos. ');
insert into tipoeventos(nombre,descripcion) values('Contaminación	','Concentración de sustancias contaminantes en el aire, el agua o los suelos, con niveles perjudiciales para la salud humana, para cultivos o especies animales,  flora y fauna en general.');
insert into tipoeventos(nombre,descripcion) values('Epidemia	','Enfermedad que ataca en una misma zona a uno o más individuos (días, semanas, meses) excediendo la frecuencia esperada: como el cólera, la fiebre tifoidea, la peste bubónica, etc. Se debe tener en cuenta que la declaración de una epidemia depende de las condiciones endémicas de la misma y de la región.');
insert into tipoeventos(nombre,descripcion) values('Escape	','Derrame o fuga de sustancias tóxicas, líquidas, sólidas o gaseosas (p. ej. fuga de gas propano), radioactivas o no, por ejemplo generados por accidentes tecnológicos, por error humano o negligencia.');
insert into tipoeventos(nombre,descripcion) values('Explosión	','Liberación brusca de una gran cantidad de energía (térmica, química o nuclear), acompañada de estruendo y rotura violenta del recipiente que la contiene, produciendo calor, luz y gases. Incluye explosiones relacionadas con errores humanos o fallas en sistemas de infraestructura. En los inventarios (típicos) con DesInventar se excluyen actos de guerra o terrorismo.');
insert into tipoeventos(nombre,descripcion) values('Granizo	','Agua congelada que desciende con violencia de las nubes en granos más o menos duros y gruesos.');
insert into tipoeventos(nombre,descripcion) values('Helada	','Disminución de la temperatura con efectos nocivos en la población, cultivos, animales, bienes y servicios. ');
insert into tipoeventos(nombre,descripcion) values('Huracán	','Depresión tropical que corresponde a una anomalía atmosférica violenta que gira a modo de torbellino caracterizado por fuentes vientos, acompañados por lluvia. ');
insert into tipoeventos(nombre,descripcion) values('Incendio','	Incendios urbanos, industriales o rurales, diferentes a incendios forestales.');
insert into tipoeventos(nombre,descripcion) values('Incendio forestal','	Incluye todos los incendios en campo abierto en áreas rurales, sobre bosques nativos, bosques cultivados, praderas, etc.');
insert into tipoeventos(nombre,descripcion) values('Inundación	','Cubrimiento con agua de un terreno donde se localicen poblaciones, cultivos, bienes o infraestructura. ');
insert into tipoeventos(nombre,descripcion) values('Lluvias	','Precipitación pluvial. Incluye lluvias puntuales, persistentes o torrenciales en una región específica, así como períodos largos de precipitaciones. ');
insert into tipoeventos(nombre,descripcion) values('Marejada','	Llegada de grandes olas al litoral, causado por huracanes, vendavales, tempestades; por coincidencia entre la dirección de los vientos y períodos de marea alta o por aumentos del nivel medio del mar durante el fenómeno El Niño. En algunos lugares se llama -creciente- o -puja- a la subida de las mareas hasta los niveles máximos quincenales y -cordonazo- a los máximos anuales. Excluye los reportes asociados con tsunami o maremoto. ');
insert into tipoeventos(nombre,descripcion) values('Neblina','	Masas de nubes que descienden hasta o se generan desde la superficie terrestre y que implican efectos sobre el transporte o cualquier otra actividad, por disminución de la visibilidad. Excluye falta de visibilidad por contaminación generada por polución, así sea generada por incendios de cualquier tipo.');
insert into tipoeventos(nombre,descripcion) values('Nevada	','Caída y acumulación de nieve. ');
insert into tipoeventos(nombre,descripcion) values('Ola de calor','	Aumento de la temperatura en una región con efectos sobre poblaciones humanas, cultivos, animales, bienes y servicios.');
insert into tipoeventos(nombre,descripcion) values('Plaga	','Proliferación de organismos que afectan a comunidades, a la agricultura, a la ganadería o a bienes perecederos almacenados. Por ejemplo: ratas, langostas, etc.');
insert into tipoeventos(nombre,descripcion) values('Pánico','	Pánico  colectivo en eventos masivos (estadios, salas de cine, etc.) que conduce a muertes, heridos y/o destrozos materiales. ');
insert into tipoeventos(nombre,descripcion) values('Sedimentación','	Deposito de material sólido producido por movimientos de masa en laderas por erosión superficial, en cauces de ríos, por inundaciones, avenidas torrenciales, avalanchas, marejadas o tsunami.');
insert into tipoeventos(nombre,descripcion) values('Sequía	','Temporada seca, sin lluvias, o con déficit de lluvias. ');
insert into tipoeventos(nombre,descripcion) values('Sismo	','Se denomina sismo o terremoto a las sacudidas bruscas del terreno causadas por el movimiento de las placas tectónicas. ');
insert into tipoeventos(nombre,descripcion) values('Tormenta  ','	Lluvia acompañada de vientos fuertes y/o de descargas eléctricas (rayos, relámpagos). ');
insert into tipoeventos(nombre,descripcion) values('Tornado	','Columna de vientos fuertes en remolino en forma de embudo que llega a la tierra desde nubes cumulonimbos. Puede incluir lluvia, granizo y rayos.');
insert into tipoeventos(nombre,descripcion) values('Tsunami	','Aplicado a olas generadas por movimiento en el fondo del mar, generado por sismos, erupciones volcánicas o deslizamientos.');
insert into tipoeventos(nombre,descripcion) values('Vendaval','	Perturbación atmosférica que genera vientos fuertes y destructivos, generalmente sin lluvia. ');
insert into tipoeventos(nombre,descripcion) values('Dengue','	Presencia del mosquito Aedes aegypti, que es el encargado de trasmitir la enfermedad del Dengue.');
insert into tipoeventos(nombre,descripcion) values('Aftosa	','Presencia de enfermedad infecciosa que provoca daños al ganado');
insert into tipoeventos(nombre,descripcion) values('Otro	','Cuando el evento no está relacionado con ninguno de los tipos de eventos anteriormente presentados. ');
